  --popover: 0 0% 10%;

  --popover-foreground: 0 0% 94%;

  --popover-border: 0 0% 16%;

  --primary: 0 84% 45%;

  --primary-foreground: 0 0% 98%;

  --secondary: 0 0% 14%;

  --secondary-foreground: 0 0% 92%;

  --muted: 280 12% 12%;

  --muted-foreground: 280 8% 70%;

